CREATE DATABASE `dbturris`;
USE `dbturris`;

CREATE TABLE `usuario` (
  `email` varchar(60) NOT NULL,
  `nombre` varchar(30) NOT NULL,
  `tipoUsuario` CHAR NOT NULL,
  `edad` int(11) NOT NULL,
  `genero` varchar(1) NOT NULL,
  `password` varchar(30) NOT NULL,
  PRIMARY KEY (`email`)
);

