CREATE TABLE `interesesusuario` (
  `idCliente` varchar(60) NOT NULL,
  `precio` varchar(25) NOT NULL,
  `ubicacion` varchar(25) NOT NULL,
  `tipo_viaje` varchar(25) NOT NULL,
  PRIMARY KEY (`idCliente`),
  CONSTRAINT `interesesusuario_x_usuario` FOREIGN KEY (`idCliente`) REFERENCES `usuario`(`email`)
);

CREATE TABLE `sitios` (
  `precio` varchar(25) NOT NULL,
  `ubicacion` varchar(25) NOT NULL,
  `tipo_viaje` varchar(25) NOT NULL,
  `descripcion` varchar(1000) NOT NULL,
  `titulo` varchar(500) NOT NULL,
  `latitud` varchar(25) DEFAULT NULL,
  `longitud` varchar(25) DEFAULT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `imagen` varchar(200) DEFAULT NULL,
  `video` varchar(400) DEFAULT NULL,
  PRIMARY KEY (`id`)
);

INSERT INTO `sitios` VALUES ('100-1000','Rural','Cultural',' El Monumento Nacional Guayabo es un área protegida de Costa Rica, conocida principalmente porque en ella se encuentra uno de los sitios arqueológicos prehispánicos más antiguos e importantes del país','Monumento Nacional Guayabo','9.970865','-83.690761',1,'public/images/sitios/monumentoGuayabo.png','https://www.youtube.com/embed/UpZeDf1GeYA'),('100-1000','Urbano','Familiar','El parque Quesada Casal es el parque central de Turrialba donde niños y adultos pueden convivir con la naturaleza y disfrutar de la misma.','Parque Quesada Casal','9.906569','-83.684173',2,'public/images/sitios/parqueQuesadaCasal.jpg','https://www.youtube.com/embed/L0IbUkFxr7s'),('100-1000','Urbano','Familiar','El jardín botánico del CATIE es un sitio donde usted puede venir y apreciar la flora del lugar y tener un momento agradable con sus acompañantes.','Jardín Botánico CATIE','9.900730','-83.668591',3,'public/images/sitios/jardinCATIE.jpg','https://www.youtube.com/embed/yvGQ4Rf-k4w'),('100-1000','Urbano','Cultural','El Museo de Turrialba Omar Salazar fue creado el 10 de noviembre de 1995, ocupa un edificio declarado Patrimonio Histórico - Arquitectónico, el museo pretende rescatar las raíces indígenas del cantón, cuenta con una exhibición arqueológica mostrando los modos de vida de las primeras poblaciones de Turrialba.','Museo Omar Salazar Obando','9.901629','-83.672596',4,'public/images/sitios/museoOmarSalazar.jpg','https://www.youtube.com/embed/zfnoQiFobeo'),('5001-15000','Rural','Deportivo','El río Turrialba es un lugar para los más animados, para aquellos que les gusta las actividades acuáticas como el rafting. Estas actividades son pensadas para personas con buena condición física y con muchos ánimos.','Río Turrialba','9.923579','-83.697948',5,'public/images/sitios/rioTurrialba.jpg','https://www.youtube.com/embed/bjSDrpuMsyE'),('5001-15000','Urbano','Negocio','Desde los platillos hasta la atención, no hay ninguna queja. Quizá 